import{_ as e,e as o,o as t,Q as l}from"./plugin-vue_export-helper.8dc630d2.js";const u='{"title":"EventLoop \u4E8B\u4EF6\u5FAA\u73AF","description":"","frontmatter":{},"headers":[{"level":3,"title":"EventLoop \u4E8B\u4EF6\u5FAA\u73AF","slug":"eventloop-\u4E8B\u4EF6\u5FAA\u73AF"}],"relativePath":"guide/js/10.EventLoop \u4E8B\u4EF6\u5FAA\u73AF.md","lastUpdated":1672843476810}',i={},a=l('<h3 id="eventloop-\u4E8B\u4EF6\u5FAA\u73AF">EventLoop \u4E8B\u4EF6\u5FAA\u73AF</h3><div class="language-"><pre><code>JS`\u662F\u5355\u7EBF\u7A0B\u7684\uFF0C\u4E3A\u4E86\u9632\u6B62\u4E00\u4E2A\u51FD\u6570\u6267\u884C\u65F6\u95F4\u8FC7\u957F\u963B\u585E\u540E\u9762\u7684\u4EE3\u7801\uFF0C\u6240\u4EE5\u4F1A\u5148\u5C06\u540C\u6B65\u4EE3\u7801\u538B\u5165\u6267\u884C\u6808\u4E2D\uFF0C\u4F9D\u6B21\u6267\u884C\uFF0C\u5C06\u5F02\u6B65\u4EE3\u7801\u63A8\u5165\u5F02\u6B65\u961F\u5217\uFF0C\u5F02\u6B65\u961F\u5217\u53C8\u5206\u4E3A\u5B8F\u4EFB\u52A1\u961F\u5217\u548C\u5FAE\u4EFB\u52A1\u961F\u5217\uFF0C\u56E0\u4E3A\u5B8F\u4EFB\u52A1\u961F\u5217\u7684\u6267\u884C\u65F6\u95F4\u8F83\u957F\uFF0C\u6240\u4EE5\u5FAE\u4EFB\u52A1\u961F\u5217\u8981\u4F18\u5148\u4E8E\u5B8F\u4EFB\u52A1\u961F\u5217\u3002\u5FAE\u4EFB\u52A1\u961F\u5217\u7684\u4EE3\u8868\u5C31\u662F\uFF0C`Promise.then`\uFF0C`MutationObserver`\uFF0C\u5B8F\u4EFB\u52A1\u7684\u8BDD\u5C31\u662F`setImmediate setTimeout setInterval\n</code></pre></div><p>JS\u8FD0\u884C\u7684\u73AF\u5883\u3002\u4E00\u822C\u4E3A\u6D4F\u89C8\u5668\u6216\u8005Node\u3002 \u5728\u6D4F\u89C8\u5668\u73AF\u5883\u4E2D\uFF0C\u6709JS \u5F15\u64CE\u7EBF\u7A0B\u548C\u6E32\u67D3\u7EBF\u7A0B\uFF0C\u4E14\u4E24\u4E2A\u7EBF\u7A0B\u4E92\u65A5\u3002 Node\u73AF\u5883\u4E2D\uFF0C\u53EA\u6709JS \u7EBF\u7A0B\u3002 \u4E0D\u540C\u73AF\u5883\u6267\u884C\u673A\u5236\u6709\u5DEE\u5F02\uFF0C\u4E0D\u540C\u4EFB\u52A1\u8FDB\u5165\u4E0D\u540CEvent Queue\u961F\u5217\u3002 \u5F53\u4E3B\u7A0B\u7ED3\u675F\uFF0C\u5148\u6267\u884C\u51C6\u5907\u597D\u5FAE\u4EFB\u52A1\uFF0C\u7136\u540E\u518D\u6267\u884C\u51C6\u5907\u597D\u7684\u5B8F\u4EFB\u52A1\uFF0C\u4E00\u4E2A\u8F6E\u8BE2\u7ED3\u675F\u3002</p><h4 id="\u6D4F\u89C8\u5668\u4E2D\u7684\u4E8B\u4EF6\u73AF\uFF08event-loop"><strong>\u6D4F\u89C8\u5668\u4E2D\u7684\u4E8B\u4EF6\u73AF\uFF08Event Loop)</strong></h4><p>\u4E8B\u4EF6\u73AF\u7684\u8FD0\u884C\u673A\u5236\u662F\uFF0C\u5148\u4F1A\u6267\u884C\u6808\u4E2D\u7684\u5185\u5BB9\uFF0C\u6808\u4E2D\u7684\u5185\u5BB9\u6267\u884C\u540E\u6267\u884C\u5FAE\u4EFB\u52A1\uFF0C\u5FAE\u4EFB\u52A1\u6E05\u7A7A\u540E\u518D\u6267\u884C\u5B8F\u4EFB\u52A1\uFF0C\u5148\u53D6\u51FA\u4E00\u4E2A\u5B8F\u4EFB\u52A1\uFF0C\u518D\u53BB\u6267\u884C\u5FAE\u4EFB\u52A1\uFF0C\u7136\u540E\u5728\u53D6\u5B8F\u4EFB\u52A1\u6E05\u5FAE\u4EFB\u52A1\u8FD9\u6837\u4E0D\u505C\u7684\u5FAA\u73AF\u3002</p><ul><li><p>eventLoop \u662F\u7531JS\u7684\u5BBF\u4E3B\u73AF\u5883\uFF08\u6D4F\u89C8\u5668\uFF09\u6765\u5B9E\u73B0\u7684\uFF1B</p></li><li><p>\u4E8B\u4EF6\u5FAA\u73AF\u53EF\u4EE5\u7B80\u5355\u7684\u63CF\u8FF0\u4E3A\u4EE5\u4E0B\u56DB\u4E2A\u6B65\u9AA4:</p><ol><li>\u51FD\u6570\u5165\u6808\uFF0C\u5F53Stack\u4E2D\u6267\u884C\u5230\u5F02\u6B65\u4EFB\u52A1\u7684\u65F6\u5019\uFF0C\u5C31\u5C06\u4ED6\u4E22\u7ED9WebAPIs,\u63A5\u7740\u6267\u884C\u540C\u6B65\u4EFB\u52A1,\u76F4\u5230Stack\u4E3A\u7A7A\uFF1B</li><li>\u6B64\u671F\u95F4WebAPIs\u5B8C\u6210\u8FD9\u4E2A\u4E8B\u4EF6\uFF0C\u628A\u56DE\u8C03\u51FD\u6570\u653E\u5165\u961F\u5217\u4E2D\u7B49\u5F85\u6267\u884C\uFF08\u5FAE\u4EFB\u52A1\u653E\u5230\u5FAE\u4EFB\u52A1\u961F\u5217\uFF0C\u5B8F\u4EFB\u52A1\u653E\u5230\u5B8F\u4EFB\u52A1\u961F\u5217\uFF09</li><li>\u6267\u884C\u6808\u4E3A\u7A7A\u65F6\uFF0CEvent Loop\u628A\u5FAE\u4EFB\u52A1\u961F\u5217\u6267\u884C\u6E05\u7A7A\uFF1B</li><li>\u5FAE\u4EFB\u52A1\u961F\u5217\u6E05\u7A7A\u540E\uFF0C\u8FDB\u5165\u5B8F\u4EFB\u52A1\u961F\u5217\uFF0C\u53D6\u961F\u5217\u7684\u7B2C\u4E00\u9879\u4EFB\u52A1\u653E\u5165Stack(\u6808\uFF09\u4E2D\u6267\u884C\uFF0C\u6267\u884C\u5B8C\u6210\u540E\uFF0C\u67E5\u770B\u5FAE\u4EFB\u52A1\u961F\u5217\u662F\u5426\u6709\u4EFB\u52A1\uFF0C\u6709\u7684\u8BDD\uFF0C\u6E05\u7A7A\u5FAE\u4EFB\u52A1\u961F\u5217\u3002\u91CD\u590D4\uFF0C\u7EE7\u7EED\u4ECE\u5B8F\u4EFB\u52A1\u4E2D\u53D6\u4EFB\u52A1\u6267\u884C\uFF0C\u6267\u884C\u5B8C\u6210\u4E4B\u540E\uFF0C\u7EE7\u7EED\u6E05\u7A7A\u5FAE\u4EFB\u52A1\uFF0C\u5982\u6B64\u53CD\u590D\u5FAA\u73AF\uFF0C\u76F4\u81F3\u6E05\u7A7A\u6240\u6709\u7684\u4EFB\u52A1\u3002</li></ol><p><img src="https://qn.huat.xyz/mac/20220828105148.awebp" alt="\u4E8B\u4EF6\u5FAA\u73AF\u6D41\u7A0B"></p></li><li><p>\u6D4F\u89C8\u5668\u4E2D\u7684\u4EFB\u52A1\u6E90(task):</p><ul><li><code>\u5B8F\u4EFB\u52A1(macrotask)</code>\uFF1A \u5BBF\u4E3B\u73AF\u5883\u63D0\u4F9B\u7684\uFF0C\u6BD4\u5982\u6D4F\u89C8\u5668 ajax\u3001setTimeout\u3001setInterval\u3001setTmmediate(\u53EA\u517C\u5BB9ie)\u3001script\u3001requestAnimationFrame\u3001messageChannel\u3001UI\u6E32\u67D3\u3001\u4E00\u4E9B\u6D4F\u89C8\u5668api</li><li><code>\u5FAE\u4EFB\u52A1(microtask)</code>\uFF1A \u8BED\u8A00\u672C\u8EAB\u63D0\u4F9B\u7684\uFF0C\u6BD4\u5982promise.then then\u3001queueMicrotask(\u57FA\u4E8Ethen)\u3001mutationObserver(\u6D4F\u89C8\u5668\u63D0\u4F9B)\u3001messageChannel \u3001mutationObersve</li></ul></li></ul><p>\u4F20\u9001\u95E8 \u261E <a href="https://juejin.cn/post/7001881781125251086" target="_blank" rel="noopener noreferrer"># \u5B8F\u4EFB\u52A1\u548C\u5FAE\u4EFB\u52A1</a></p><h4 id="node-\u73AF\u5883\u4E2D\u7684\u4E8B\u4EF6\u73AF\uFF08event-loop"><strong>Node \u73AF\u5883\u4E2D\u7684\u4E8B\u4EF6\u73AF\uFF08Event Loop)</strong></h4><p><code>Node</code>\u662F\u57FA\u4E8EV8\u5F15\u64CE\u7684\u8FD0\u884C\u5728\u670D\u52A1\u7AEF\u7684<code>JavaScript</code>\u8FD0\u884C\u73AF\u5883\uFF0C\u5728\u5904\u7406\u9AD8\u5E76\u53D1\u3001I/O\u5BC6\u96C6(\u6587\u4EF6\u64CD\u4F5C\u3001\u7F51\u7EDC\u64CD\u4F5C\u3001\u6570\u636E\u5E93\u64CD\u4F5C\u7B49)\u573A\u666F\u6709\u660E\u663E\u7684\u4F18\u52BF\u3002\u867D\u7136\u7528\u5230\u4E5F\u662FV8\u5F15\u64CE\uFF0C\u4F46\u7531\u4E8E\u670D\u52A1\u76EE\u7684\u548C\u73AF\u5883\u4E0D\u540C\uFF0C\u5BFC\u81F4\u4E86\u5B83\u7684API\u4E0E\u539F\u751FJS\u6709\u4E9B\u533A\u522B\uFF0C\u5176Event Loop\u8FD8\u8981\u5904\u7406\u4E00\u4E9BI/O\uFF0C\u6BD4\u5982\u65B0\u7684\u7F51\u7EDC\u8FDE\u63A5\u7B49\uFF0C\u6240\u4EE5Node\u7684Event Loop(\u4E8B\u4EF6\u73AF\u673A\u5236)\u4E0E\u6D4F\u89C8\u5668\u7684\u662F\u4E0D\u592A\u4E00\u6837\u3002</p><p><img src="https://qn.huat.xyz/mac/20220828105216.awebp" alt="2020120317343116.png"></p><p>\u6267\u884C\u987A\u5E8F\u5982\u4E0B\uFF1A</p><ul><li><code>timers</code>: \u8BA1\u65F6\u5668\uFF0C\u6267\u884CsetTimeout\u548CsetInterval\u7684\u56DE\u8C03</li><li><code>pending callbacks</code>: \u6267\u884C\u5EF6\u8FDF\u5230\u4E0B\u4E00\u4E2A\u5FAA\u73AF\u8FED\u4EE3\u7684 I/O \u56DE\u8C03</li><li><code>idle, prepare</code>: \u961F\u5217\u7684\u79FB\u52A8\uFF0C\u4EC5\u7CFB\u7EDF\u5185\u90E8\u4F7F\u7528</li><li><code>poll\u8F6E\u8BE2</code>: \u68C0\u7D22\u65B0\u7684 I/O \u4E8B\u4EF6;\u6267\u884C\u4E0E I/O \u76F8\u5173\u7684\u56DE\u8C03\u3002\u4E8B\u5B9E\u4E0A\u9664\u4E86\u5176\u4ED6\u51E0\u4E2A\u9636\u6BB5\u5904\u7406\u7684\u4E8B\u60C5\uFF0C\u5176\u4ED6\u51E0\u4E4E\u6240\u6709\u7684\u5F02\u6B65\u90FD\u5728\u8FD9\u4E2A\u9636\u6BB5\u5904\u7406\u3002</li><li><code>check</code>: \u6267\u884C<code>setImmediate</code>\u56DE\u8C03\uFF0CsetImmediate\u5728\u8FD9\u91CC\u6267\u884C</li><li><code>close callbacks</code>: \u6267\u884C<code>close</code>\u4E8B\u4EF6\u7684<code>callback</code>\uFF0C\u4E00\u4E9B\u5173\u95ED\u7684\u56DE\u8C03\u51FD\u6570\uFF0C\u5982\uFF1Asocket.on(&#39;close&#39;, ...)</li></ul>',12),c=[a];function s(n,p,d,r,_,m){return t(),o("div",null,c)}var h=e(i,[["render",s]]);export{u as __pageData,h as default};
