import{_ as l,e as t,o as p,Q as i}from"./plugin-vue_export-helper.8dc630d2.js";const m='{"title":"\u95ED\u5305\u7684\u4E24\u5927\u4F5C\u7528\uFF1A\u4FDD\u5B58/\u4FDD\u62A4","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u95ED\u5305\u7684\u4E24\u5927\u4F5C\u7528\uFF1A\u4FDD\u5B58/\u4FDD\u62A4","slug":"\u95ED\u5305\u7684\u4E24\u5927\u4F5C\u7528\uFF1A\u4FDD\u5B58-\u4FDD\u62A4"}],"relativePath":"guide/js/6.\u95ED\u5305\u7684\u4E24\u5927\u4F5C\u7528\uFF1A\u4FDD\u5B58\u3001\u4FDD\u62A4.md","lastUpdated":1672843476810}',o={},e=i('<h3 id="\u95ED\u5305\u7684\u4E24\u5927\u4F5C\u7528\uFF1A\u4FDD\u5B58-\u4FDD\u62A4">\u95ED\u5305\u7684\u4E24\u5927\u4F5C\u7528\uFF1A\u4FDD\u5B58/\u4FDD\u62A4</h3><ul><li><p><strong>\u95ED\u5305\u7684\u6982\u5FF5</strong></p><p>\u51FD\u6570\u6267\u884C\u65F6\u5F62\u6210\u7684\u79C1\u6709\u4E0A\u4E0B\u6587EC(FN)\uFF0C\u6B63\u5E38\u60C5\u51B5\u4E0B\uFF0C\u4EE3\u7801\u6267\u884C\u5B8C\u4F1A\u51FA\u6808\u540E\u91CA\u653E;\u4F46\u662F\u7279\u6B8A\u60C5\u51B5\u4E0B\uFF0C\u5982\u679C\u5F53\u524D\u79C1\u6709\u4E0A\u4E0B\u6587\u4E2D\u7684\u67D0\u4E2A\u4E1C\u897F\u88AB\u4E0A\u4E0B\u6587\u4EE5\u5916\u7684\u4E8B\u7269\u5360\u7528\u4E86\uFF0C\u5219\u4E0A\u4E0B\u6587\u4E0D\u4F1A\u51FA\u6808\u91CA\u653E\uFF0C\u4ECE\u800C\u5F62\u6210\u4E0D\u9500\u6BC1\u7684\u4E0A\u4E0B\u6587\u3002 \u51FD\u6570\u6267\u884C\u51FD\u6570\u6267\u884C\u8FC7\u7A0B\u4E2D\uFF0C\u4F1A\u5F62\u6210\u4E00\u4E2A\u5168\u65B0\u7684\u79C1\u6709\u4E0A\u4E0B\u6587\uFF0C\u53EF\u80FD\u4F1A\u88AB\u91CA\u653E\uFF0C\u53EF\u80FD\u4E0D\u4F1A\u88AB\u91CA\u653E\uFF0C\u4E0D\u8BBA\u91CA\u653E\u4E0E\u5426\uFF0C\u4ED6\u7684\u4F5C\u7528\u662F\uFF1A</p></li></ul><p>\uFF081\uFF09\u4FDD\u62A4\uFF1A\u5212\u5206\u4E00\u4E2A\u72EC\u7ACB\u7684\u4EE3\u7801\u6267\u884C\u533A\u57DF\uFF0C\u5728\u8FD9\u4E2A\u533A\u57DF\u4E2D\u6709\u81EA\u5DF1\u79C1\u6709\u53D8\u91CF\u5B58\u50A8\u7684\u7A7A\u95F4\uFF0C\u4FDD\u62A4\u81EA\u5DF1\u7684\u79C1\u6709\u53D8\u91CF\u4E0D\u53D7\u5916\u754C\u5E72\u6270\uFF08\u64CD\u4F5C\u81EA\u5DF1\u7684\u79C1\u6709\u53D8\u91CF\u548C\u5916\u754C\u6CA1\u6709\u5173\u7CFB\uFF09\uFF1B</p><p>\uFF082\uFF09\u4FDD\u5B58\uFF1A\u5982\u679C\u5F53\u524D\u4E0A\u4E0B\u6587\u4E0D\u88AB\u91CA\u653E\u3010\u53EA\u8981\u4E0A\u4E0B\u6587\u4E2D\u7684\u67D0\u4E2A\u4E1C\u897F\u88AB\u5916\u90E8\u5360\u7528\u5373\u53EF\u3011\uFF0C\u5219\u5B58\u50A8\u7684\u8FD9\u4E9B\u79C1\u6709\u53D8\u91CF\u4E5F\u4E0D\u4F1A\u88AB\u91CA\u653E\uFF0C\u53EF\u4EE5\u4F9B\u5176\u4E0B\u7EA7\u4E0A\u4E0B\u6587\u4E2D\u8C03\u53D6\u4F7F\u7528\uFF0C\u76F8\u5F53\u4E8E\u628A\u4E00\u4E9B\u503C\u4FDD\u5B58\u8D77\u6765\u4E86\uFF1B</p><p>\u6211\u4EEC\u628A\u51FD\u6570\u6267\u884C\u5F62\u6210\u79C1\u6709\u4E0A\u4E0B\u6587\uFF0C\u6765\u4FDD\u62A4\u548C\u4FDD\u5B58\u79C1\u6709\u53D8\u91CF\u673A\u5236\u79F0\u4E3A<code>\u95ED\u5305</code>\u3002</p><blockquote><p>\u95ED\u5305\u662F\u6307\u6709\u6743\u8BBF\u95EE\u53E6\u4E00\u4E2A\u51FD\u6570\u4F5C\u7528\u57DF\u4E2D\u7684\u53D8\u91CF\u7684\u51FD\u6570--\u300AJavaScript\u9AD8\u7EA7\u7A0B\u5E8F\u8BBE\u8BA1\u300B</p></blockquote><p><strong>\u7A0D\u5168\u9762\u7684\u56DE\u7B54</strong>\uFF1A \u5728js\u4E2D\u53D8\u91CF\u7684\u4F5C\u7528\u57DF\u5C5E\u4E8E\u51FD\u6570\u4F5C\u7528\u57DF, \u5728\u51FD\u6570\u6267\u884C\u5B8C\u540E,\u4F5C\u7528\u57DF\u5C31\u4F1A\u88AB\u6E05\u7406,\u5185\u5B58\u4E5F\u4F1A\u968F\u4E4B\u88AB\u56DE\u6536,\u4F46\u662F\u7531\u4E8E\u95ED\u5305\u51FD\u6570\u662F\u5EFA\u7ACB\u5728\u51FD\u6570\u5185\u90E8\u7684\u5B50\u51FD\u6570, \u7531\u4E8E\u5176\u53EF\u8BBF\u95EE\u4E0A\u7EA7\u4F5C\u7528\u57DF,\u5373\u4F7F\u4E0A\u7EA7\u51FD\u6570\u6267\u884C\u5B8C, \u4F5C\u7528\u57DF\u4E5F\u4E0D\u4F1A\u968F\u4E4B\u9500\u6BC1, \u8FD9\u65F6\u7684\u5B50\u51FD\u6570(\u4E5F\u5C31\u662F\u95ED\u5305),\u4FBF\u62E5\u6709\u4E86\u8BBF\u95EE\u4E0A\u7EA7\u4F5C\u7528\u57DF\u4E2D\u53D8\u91CF\u7684\u6743\u9650,\u5373\u4F7F\u4E0A\u7EA7\u51FD\u6570\u6267\u884C\u5B8C\u540E\u4F5C\u7528\u57DF\u5185\u7684\u503C\u4E5F\u4E0D\u4F1A\u88AB\u9500\u6BC1\u3002</p><ul><li><p><strong>\u95ED\u5305\u7684\u7279\u6027</strong>\uFF1A</p><ul><li><p>1\u3001\u5185\u90E8\u51FD\u6570\u53EF\u4EE5\u8BBF\u95EE\u5B9A\u4E49\u4ED6\u4EEC\u5916\u90E8\u51FD\u6570\u7684\u53C2\u6570\u548C\u53D8\u91CF\u3002(\u4F5C\u7528\u57DF\u94FE\u7684\u5411\u4E0A\u67E5\u627E\uFF0C\u628A\u5916\u56F4\u7684\u4F5C\u7528\u57DF\u4E2D\u7684\u53D8\u91CF\u503C\u5B58\u50A8\u5728\u5185\u5B58\u4E2D\u800C\u4E0D\u662F\u5728\u51FD\u6570\u8C03\u7528\u5B8C\u6BD5\u540E\u9500\u6BC1)\u8BBE\u8BA1\u79C1\u6709\u7684\u65B9\u6CD5\u548C\u53D8\u91CF\uFF0C\u907F\u514D\u5168\u5C40\u53D8\u91CF\u7684\u6C61\u67D3\u3002</p><p>1.1.\u95ED\u5305\u662F\u5BC6\u95ED\u7684\u5BB9\u5668\uFF0C\uFF0C\u7C7B\u4F3C\u4E8Eset\u3001map\u5BB9\u5668\uFF0C\u5B58\u50A8\u6570\u636E\u7684</p><p>1.2.\u95ED\u5305\u662F\u4E00\u4E2A\u5BF9\u8C61\uFF0C\u5B58\u653E\u6570\u636E\u7684\u683C\u5F0F\u4E3A key-value \u5F62\u5F0F</p></li><li><p>2\u3001\u51FD\u6570\u5D4C\u5957\u51FD\u6570</p></li><li><p>3\u3001\u672C\u8D28\u662F\u5C06\u51FD\u6570\u5185\u90E8\u548C\u5916\u90E8\u8FDE\u63A5\u8D77\u6765\u3002\u4F18\u70B9\u662F\u53EF\u4EE5\u8BFB\u53D6\u51FD\u6570\u5185\u90E8\u7684\u53D8\u91CF\uFF0C\u8BA9\u8FD9\u4E9B\u53D8\u91CF\u7684\u503C\u59CB\u7EC8\u4FDD\u5B58\u5728\u5185\u5B58\u4E2D\uFF0C\u4E0D\u4F1A\u5728\u51FD\u6570\u88AB\u8C03\u7528\u4E4B\u540E\u81EA\u52A8\u6E05\u9664</p></li></ul></li><li><p><strong>\u95ED\u5305\u5F62\u6210\u7684\u6761\u4EF6</strong>\uFF1A</p><ol><li>\u51FD\u6570\u7684\u5D4C\u5957</li><li>\u5185\u90E8\u51FD\u6570\u5F15\u7528\u5916\u90E8\u51FD\u6570\u7684\u5C40\u90E8\u53D8\u91CF\uFF0C\u5EF6\u957F\u5916\u90E8\u51FD\u6570\u7684\u53D8\u91CF\u751F\u547D\u5468\u671F</li></ol></li><li><p><strong>\u95ED\u5305\u7684\u7528\u9014</strong>\uFF1A</p><ol><li>\u6A21\u4EFF\u5757\u7EA7\u4F5C\u7528\u57DF</li><li>\u4FDD\u62A4\u5916\u90E8\u51FD\u6570\u7684\u53D8\u91CF \u80FD\u591F\u8BBF\u95EE\u51FD\u6570\u5B9A\u4E49\u65F6\u6240\u5728\u7684\u8BCD\u6CD5\u4F5C\u7528\u57DF(\u963B\u6B62\u5176\u88AB\u56DE\u6536)</li><li>\u5C01\u88C5\u79C1\u6709\u5316\u53D8\u91CF</li><li>\u521B\u5EFA\u6A21\u5757</li></ol></li><li><p><strong>\u95ED\u5305\u5E94\u7528\u573A\u666F</strong></p><p>\u95ED\u5305\u7684\u4E24\u4E2A\u573A\u666F\uFF0C\u95ED\u5305\u7684\u4E24\u5927\u4F5C\u7528\uFF1A<code>\u4FDD\u5B58/\u4FDD\u62A4</code>\u3002 \u5728\u5F00\u53D1\u4E2D, \u5176\u5B9E\u6211\u4EEC\u968F\u5904\u53EF\u89C1\u95ED\u5305\u7684\u8EAB\u5F71, \u5927\u90E8\u5206\u524D\u7AEFJavaScript \u4EE3\u7801\u90FD\u662F\u201C\u4E8B\u4EF6\u9A71\u52A8\u201D\u7684,\u5373\u4E00\u4E2A\u4E8B\u4EF6\u7ED1\u5B9A\u7684\u56DE\u8C03\u65B9\u6CD5; \u53D1\u9001ajax\u8BF7\u6C42\u6210\u529F|\u5931\u8D25\u7684\u56DE\u8C03;setTimeout\u7684\u5EF6\u65F6\u56DE\u8C03;\u6216\u8005\u4E00\u4E2A\u51FD\u6570\u5185\u90E8\u8FD4\u56DE\u53E6\u4E00\u4E2A\u533F\u540D\u51FD\u6570,\u8FD9\u4E9B\u90FD\u662F\u95ED\u5305\u7684\u5E94\u7528\u3002</p></li><li><p><strong>\u95ED\u5305\u7684\u4F18\u70B9</strong>\uFF1A\u5EF6\u957F\u5C40\u90E8\u53D8\u91CF\u7684\u751F\u547D\u5468\u671F</p></li><li><p><strong>\u95ED\u5305\u7F3A\u70B9</strong>\uFF1A\u4F1A\u5BFC\u81F4\u51FD\u6570\u7684\u53D8\u91CF\u4E00\u76F4\u4FDD\u5B58\u5728\u5185\u5B58\u4E2D\uFF0C\u8FC7\u591A\u7684\u95ED\u5305\u53EF\u80FD\u4F1A\u5BFC\u81F4\u5185\u5B58\u6CC4\u6F0F</p></li></ul>',8),_=[e];function s(r,n,a,c,d,g){return p(),t("div",null,_)}var f=l(o,[["render",s]]);export{m as __pageData,f as default};
